/*
* @name: 过滤地址栏中的删除掉等字
* @Author: ccy
* @version: 0.0.2
* @description: 过滤掉蓝奏云或阿里云链接中的删除掉等字
* @include: *
*/

;(()=>{ 
window.addEventListener('load', () => {
// 要过滤的域名
let domain = [
     'lanzou',  //  蓝奏
     'aliyundrive'  // 阿里云
];
// 要过滤的文字
let del = [
    '%E5%88%A0', // 删
    '%E9%99%A4', // 除
    '%E6%8E%89' // 掉
];
let url = location.href ;
if(domain.filter(v => url.indexOf(v) > -1).length > 0 && del.filter(v => url.indexOf(v) > -1).length > 0){
    url = url.substring(url.lastIndexOf('=') + 1);
    let reg ;
    del.forEach( v => {
      reg = new RegExp(v,'gi');
      url = url.replace(reg,'');
    });
    url = url.replace(/%2F/g,'/').replace(/%3A/g,':');
    if(url.toLowerCase().indexOf('http') == -1){
      url = 'http://' + url;
    }
    location.href = url
  }
})})();

